mapper.select:
SELECT id ,parent_id ,name ,icon ,is_leaf FROM sys_permission WHERE 1=1
AND parent_id = #{parentId,jdbcType=VARCHAR}
AND parent_id is null

SELECT * FROM ( SELECT p.* FROM sys_permission p WHERE (exists( select a.id from sys_role_permission a join sys_role b on a.role_id = b.id join sys_user_role c on c.role_id = b.id join sys_user d on d.id = c.user_id where p.id = a.permission_id AND d.username = #{username,jdbcType=VARCHAR} ) or (p.url like '%:code' and p.url like '/online%' and p.hidden = 1) or p.url = '/online') and p.del_flag = 0
UNION SELECT p.* FROM sys_permission p WHERE exists( select a.id from sys_depart_role_permission a join sys_depart_role b on a.role_id = b.id join sys_depart_role_user c on c.drole_id = b.id join sys_user d on d.id = c.user_id where p.id = a.permission_id AND d.username = #{username,jdbcType=VARCHAR} ) and p.del_flag = 0
) h order by h.sort_no ASC

select sum(cnt) from (
  select count(*) as cnt
	from sys_role_permission a
	join sys_permission b on a.permission_id = b.id
	join sys_role c on a.role_id = c.id
	join sys_user_role d on d.role_id = c.id
	join sys_user e on d.user_id = e.id
	where e.username = #{username}

		and b.id =  #{permission.id}


		and b.url =  #{permission.url}

union all
  select count(*) as cnt
	from sys_permission z
    join sys_depart_role_permission y on z.id = y.permission_id
	join sys_depart_role x on y.role_id = x.id
	join sys_depart_role_user w on w.drole_id = x.id
	join sys_user v on w.user_id = v.id
	where v.username = #{username}

		and z.id =  #{permission.id}


		and z.url =  #{permission.url}

) temp
