mapper.select:
select * from sys_user where del_flag = 0

	and id in (select user_id from sys_user_depart where dep_id in

		#{id}

	)


	and username = #{username}

mapper.update:
delete from sys_user_role
where role_id in

	#{id}

delete from sys_role_permission
where role_id in

	#{id}

mapper.select:
select * from sys_user where del_flag = 0

	and id in (select user_id from sys_user_depart where dep_id in

		#{id}

	)


	and username != #{username}
