mapper.select:
   select id,titile,create_time,priority,bus_id,open_type,open_page,sender,send_time,msg_content from sys_announcement 
   where send_status = '1' 
   and del_flag = '0' 
   and msg_category = #{msgCategory} 
   and create_time >= #{beginDate}

		and tenant_id = #{tenantId}

   and id IN ( select annt_id from sys_announcement_send where user_id = #{userId} and read_flag = 0 and create_time >= #{beginDate})
   order by create_time DESC

select a.id, a.titile, a.msg_content, a.sender, a.priority, a.msg_category, a.msg_type, a.send_status, a.send_time, a.bus_type, a.bus_id, a.open_type, a.open_page, a.msg_abstract, a.dt_task_id, b.read_flag, b.star_flag, b.id as send_id from sys_announcement a join sys_announcement_send b on b.annt_id = a.id where a.send_status = '1' and a.del_flag = '0' and b.user_id = #{userId}
and a.sender = #{fromUser}
and a.create_time >= #{beginDate} and b.create_time >= #{beginDate}
and a.create_time <= #{endDate}
and b.star_flag = #{starFlag}
order by b.read_flag ASC, a.create_time DESC
