mapper.select:
select sas.id, sas.annt_id, sas.user_id, sas.read_flag, sa.titile as titile, sa.msg_content as msg_content, sa.sender as sender, sa.priority as priority, sa.msg_category, sa.send_time as send_time, sa.bus_id as bus_id, sa.bus_type as bus_type, sa.open_type as open_type, sa.open_page as open_page, sa.msg_abstract from sys_announcement_send sas left join sys_announcement sa ON sas.annt_id = sa.id where sa.send_status = '1' and sa.del_flag = '0' and sas.user_id = #{announcementSendModel.userId}
and sa.titile LIKE concat(concat('%',#{announcementSendModel.titile}),'%')
and sa.sender LIKE concat(concat('%',#{announcementSendModel.sender}),'%')
and sas.read_flag = #{announcementSendModel.readFlag}
and sa.bus_type = #{announcementSendModel.busType}
and (sa.bus_type != 'bpm' or sa.bus_type is null)
and sa.msg_category = #{announcementSendModel.msgCategory}
order by sas.read_flag,sa.send_time desc

mapper.update:
update sys_announcement_send set read_flag = 1
 where user_id = #{userId} 
	and annt_id in

	#{id}
