mapper.select:
select code,
	   name as "title",
	   id as "key",
	   (case when has_child = '1' then 0 else 1 end) as isLeaf,
	   pid as parentId
	   from sys_category
	where pid = #{pid}


			and code = #{query.code}



			and name = #{query.name}



			and id = #{query.id}



			and create_by = #{query.createBy}



			and sys_org_code = #{query.sysOrgCode}
