mapper.update:
UPDATE platform_order
    JOIN shop s ON platform_order.shop_id = s.id
    JOIN client c ON s.owner_id = c.id
SET purchase_invoice_number = NULL
WHERE purchase_invoice_number = #{invoiceNumber}
    AND c.id = #{clientId};

UPDATE platform_order
SET purchase_invoice_number = NULL
WHERE purchase_invoice_number IN

    #{invoiceNumber}
;

UPDATE platform_order
SET purchase_invoice_number = #{invoiceNumber}
WHERE id IN

    #{orderId}

mapper.select:
SELECT *
FROM platform_order
WHERE shipping_invoice_number = #{invoiceNumber};

WITH processedByUser AS (
    SELECT invoice_number
    FROM shipping_invoice si
    WHERE create_by = #{username}
    UNION ALL
    SELECT invoice_number
    FROM purchase_order po
    WHERE create_by = #{username}
), processed AS (
    SELECT COUNT(*) AS processed
    FROM platform_order
    WHERE erp_status = 4
    AND IF(#{showAllData} = true,
           shipping_invoice_number IS NOT NULL,
           (shipping_invoice_number IN (SELECT invoice_number FROM processedByUser) OR purchase_invoice_number IN (SELECT invoice_number FROM processedByUser))
        )
    AND shipping_time BETWEEN #{start} AND #{end}
), processing AS (
    SELECT COUNT(*) AS processing
    FROM platform_order
    WHERE erp_status IN (1,2,3)
    AND order_time <= #{end}
    AND IF(#{showAllData} = true,
           shipping_invoice_number IS NOT NULL,
           shipping_invoice_number IN (SELECT invoice_number FROM processedByUser) OR purchase_invoice_number IN (SELECT invoice_number FROM processedByUser)
        )
)
SELECT processed, processing
FROM processed, processing;

mapper.update:
UPDATE platform_order_delete
SET recipient = UUID()
WHERE create_time < DATE_SUB(NOW(), INTERVAL #{period} YEAR);

mapper.select:
SELECT po.platform_order_id
FROM platform_order po
    JOIN shop ON po.shop_id = shop.id
    JOIN country c ON po.country = c.name_en
    JOIN platform_order_content poc ON po.id = poc.platform_order_id
    JOIN sku s ON poc.sku_id = s.id
WHERE po.erp_status IN (1,2)
AND po.can_send = 2
AND c.code IN

    #{country}

AND shop.erp_code = #{shop}
AND s.erp_code IN

    #{sku}

AND order_time >= #{startDate}
AND order_time <= #{endDate}
AND shipping_invoice_number IS NULL;

SELECT po.platform_order_id
FROM platform_order po
    JOIN shop ON po.shop_id = shop.id
    JOIN country c ON po.country = c.name_en
    JOIN platform_order_content poc ON po.id = poc.platform_order_id
    JOIN sku s ON poc.sku_id = s.id
WHERE po.erp_status IN (1,2)
AND po.can_send = 2
AND c.code NOT IN

    #{country}

AND shop.erp_code = #{shop}
AND s.erp_code IN

    #{sku}

AND order_time >= #{startDate}
AND order_time <= #{endDate}
AND shipping_invoice_number IS NULL;

WITH skusInAbnormalOrders AS ( SELECT poc.sku_id , poc.platform_order_id, poc.erp_status FROM platform_order po JOIN platform_order_content poc ON po.id = poc.platform_order_id JOIN shop s ON po.shop_id = s.id WHERE can_send = 2 AND po.erp_status IN (1,2) AND po.order_time >= NOW() - INTERVAL 5 DAY AND s.erp_code IN
#{shop}
), orders_with_bad_sku AS ( SELECT DISTINCT CASE WHEN s.id IS NULL AND sao.erp_status <> 5 THEN sao.platform_order_id END as order_id FROM skusInAbnormalOrders sao LEFT JOIN sku s ON sao.sku_id = s.id ), orders_with_skus AS ( SELECT DISTINCT poc.platform_order_id as id FROM platform_order_content poc JOIN sku s ON poc.sku_id = s.id JOIN platform_order po ON poc.platform_order_id = po.id WHERE po.can_send IN (1,2) AND po.can_send = 2 AND po.order_time >= NOW() - INTERVAL 5 DAY AND s.erp_code IN
#{sku}
AND poc.erp_status <> 5 AND poc.warehouse_name != '法国巴黎仓库-唯客路' ), orders_ready AS ( SELECT po.id as id FROM platform_order po JOIN shop s ON po.shop_id = s.id LEFT JOIN orders_with_bad_sku owbs ON po.id = owbs.order_id WHERE owbs.order_id IS NULL AND po.erp_status IN (1,2) AND po.can_send = 2 AND po.order_time >= NOW() - INTERVAL 5 DAY AND s.erp_code IN
#{shop}
) SELECT ors.id FROM orders_ready ors WHERE ors.id NOT IN (SELECT * FROM orders_with_skus);

SELECT DISTINCT poc.platform_order_id FROM platform_order_content poc JOIN sku s ON poc.sku_id = s.id JOIN platform_order po ON poc.platform_order_id = po.id WHERE po.can_send IN (1,2) AND po.can_send = 2 AND po.order_time >= NOW() - INTERVAL 5 DAY AND s.erp_code IN
#{sku}
AND poc.erp_status <> 5 AND poc.warehouse_name = '法国巴黎仓库-唯客路';
