SELECT * FROM platform_order WHERE IF(shipping_invoice_number IS NOT NULL, shipping_invoice_number LIKE '%%%%-%%-2%%%', shipping_invoice_number IS NULL) AND purchase_invoice_number IS NULL AND product_available = 0 AND virtual_product_available = 0 AND erp_status IN
#{erpStatus}
