#{shopId} ;

SELECT id, order_time, platform_order_number
FROM platform_order
WHERE erp_status IN

    #{erpStatus}

AND (shipping_invoice_number IS NULL
    OR (shipping_invoice_number LIKE '%%%%-%%-2%%%'
        AND purchase_invoice_number IS NULL))
AND shop_id IN

    #{shopId}

ORDER BY ${column} ${order}

    LIMIT #{offset}, #{size}

;

WITH sku_stats AS ( SELECT sku.id AS sku_id, COUNT(sw.sku_id) AS weight_count, COUNT(sp.sku_id) AS price_count FROM sku LEFT JOIN sku_weight sw ON sku.id = sw.sku_id LEFT JOIN sku_price sp ON sku.id = sp.sku_id GROUP BY sku.id ), uninvoiced_orders AS ( SELECT po.id AS id, s.name AS shop_id, po.platform_order_number AS platform_order_number, po.order_time AS order_time, po.country AS country, MIN( CASE WHEN poc.id IS NULL OR sku.id IS NULL THEN -1 WHEN po.shipping_invoice_number IS NULL AND (po.logistic_channel_name IS NULL OR po.logistic_channel_name = '') AND (po.invoice_logistic_channel_name IS NULL OR po.invoice_logistic_channel_name = '') THEN -1 WHEN po.shipping_invoice_number IS NULL AND stats.weight_count = 0 THEN -1 WHEN po.shipping_invoice_number IS NOT NULL AND si.paid_amount > 0.00 THEN 2 WHEN po.shipping_invoice_number IS NOT NULL AND si.paid_amount = 0.00 THEN 1 ELSE 0 END ) AS shipping_available, MIN( CASE WHEN poc.id IS NULL OR sku.id IS NULL THEN -1 WHEN po.purchase_invoice_number IS NULL AND stats.price_count = 0 THEN -1 WHEN po.purchase_invoice_number IS NOT NULL AND pur.paid_amount > 0.00 THEN 2 WHEN po.purchase_invoice_number IS NOT NULL AND pur.paid_amount = 0.00 THEN 1 ELSE 0 END ) AS purchase_available, CASE WHEN po.product_available = 0 AND po.virtual_product_available = 1 AND po.purchase_invoice_number IS NULL THEN 2 ELSE COALESCE(po.product_available, 0) END AS product_available FROM platform_order po JOIN shop s ON po.shop_id = s.id LEFT JOIN platform_order_content poc ON po.id = poc.platform_order_id LEFT JOIN sku ON poc.sku_id = sku.id LEFT JOIN sku_stats stats ON sku.id = stats.sku_id LEFT JOIN purchase_order pur ON pur.invoice_number = po.purchase_invoice_number LEFT JOIN shipping_invoice si ON po.shipping_invoice_number = si.invoice_number WHERE po.erp_status IN
#{erpStatus}
AND poc.erp_status <> 5
AND po.order_time >= #{startDate} AND po.order_time <= #{endDate}
AND (po.shipping_invoice_number IS NULL OR (po.shipping_invoice_number LIKE '%%%%-%%-2%%%' AND po.purchase_invoice_number IS NULL ) ) AND shop_id IN
#{shopId}
GROUP BY po.id, po.platform_order_number, po.product_available, po.virtual_product_available, po.purchase_invoice_number ) SELECT *, COUNT(*) OVER() AS total_count FROM uninvoiced_orders WHERE product_available IN
#{productStatus}
AND shipping_available IN
#{shippingStatus}
AND purchase_available IN
#{purchaseStatus}
ORDER BY ${column} ${order} LIMIT #{offset}, #{size};

