mapper.select:
SELECT platform_order_id
FROM platform_order
WHERE shipping_invoice_number IN

    #{invoiceNumber}

   OR purchase_invoice_number IN

    #{invoiceNumber}

SELECT count(po.id)
FROM platform_order po
    JOIN platform_order_content poc ON po.id = poc.platform_order_id
    JOIN sku s ON poc.sku_id = s.id
    JOIN logistic_channel lc ON po.logistic_channel_name = lc.zh_name
WHERE po.erp_status IN

            #{erpStatus}

    AND po.shop_id IN

            #{shopId}

    AND (
        lc.warehouse_in_china IN

                #{warehouse}

        OR po.logistic_channel_name = ''
        OR po.logistic_channel_name IS NULL
    )

        AND po.shipping_time BETWEEN #{start} AND #{end}


        AND po.order_time BETWEEN #{start} AND #{end}

WITH orders AS ( SELECT po.id as id, po.shop_id as shop_id, po.platform_order_number as platform_order_number, po.platform_order_id as platform_order_id, po.logistic_channel_name as logistic_channel_name, po.invoice_logistic_channel_name as invoice_logistic_channel_name, po.tracking_number as tracking_number, po.order_time as order_time, po.country as country, po.fret_fee as fret_fee, po.order_service_fee as order_service_fee, po.erp_status as erp_status, po.can_send as can_send, po.product_available as product_available FROM platform_order po JOIN logistic_channel lc ON po.logistic_channel_name = lc.zh_name WHERE po.erp_status IN
#{erpStatus}
AND po.shop_id IN
#{shopId}
AND ( lc.warehouse_in_china IN
#{warehouse}
OR po.logistic_channel_name = '' OR po.logistic_channel_name IS NULL )
AND po.shipping_time BETWEEN #{start} AND #{end}
AND po.order_time BETWEEN #{start} AND #{end}
AND po.shipping_invoice_number IS NULL ORDER BY ${column} ${order} LIMIT #{offset}, #{size} ) SELECT orders.*, CASE WHEN EXISTS ( SELECT 1 FROM platform_order_content poc JOIN sku s ON poc.sku_id = s.id WHERE poc.platform_order_id = orders.id AND s.is_synced = 0 AND poc.erp_status <> 5 ) THEN 1 ELSE 0 END as has_desynced_sku FROM orders
