mapper.update:
UPDATE purchase_order
SET status = 'confirmed'
WHERE id = #{purchaseID}

UPDATE purchase_order
SET status = 'purchasing'
WHERE id = #{purchaseID}

mapper.sql:
SELECT p.*
FROM purchase_order p

    WHERE p.client_id = #{clientID}

mapper.select:
SELECT payment_document
FROM purchase_order
WHERE id = #{purchaseID}

SELECT invoice_number
FROM purchase_order
WHERE id = #{purchaseID}
