mapper.insert:
INSERT INTO sku_declared_value(id, create_by, create_time, sku_id, declared_value, effective_date)
VALUES

    (
    #{item.id}, #{item.createBy}, #{item.createTime},
    skuErpToId(#{item.erpCode}), #{item.declaredValue}, #{item.effectiveDate}
    )

mapper.select:
SELECT declared_value, effective_date
FROM sku_declared_value
WHERE sku_id = #{skuId}
ORDER BY effective_date DESC
LIMIT 1;
