mapper.select:
SELECT s.id FROM sku s LEFT JOIN sku_price sp ON s.id = sp.sku_id WHERE sp.id IS NULL AND s.id IN
#{id}
LIMIT 1;

SELECT erp_code
FROM sku
WHERE id IN

    #{skuId}
;

SELECT sku_id as ID, sku.erp_code as erp_code, SUM(quantity) AS quantity FROM platform_order_content JOIN sku ON platform_order_content.sku_id = sku.id WHERE platform_order_id IN
#{orderId}
AND erp_status IN ('1','2','3') AND product_available = 0 AND virtual_product_available = 0 GROUP BY sku_id;

SELECT COUNT(*) AS total
FROM sku s
WHERE s.status = 3;

SELECT COUNT(*) AS total
FROM sku s
WHERE s.status = 3
AND (

        s.erp_code REGEXP #{erpCodes}



            AND

        s.zh_name REGEXP #{zhNames}



            AND

        s.en_name REGEXP #{enNames}

    );

WITH latestSkuWeights AS ( SELECT sku_id, weight, effective_date, ROW_NUMBER() OVER (PARTITION BY sku_id ORDER BY effective_date DESC) AS rn FROM sku_weight ) SELECT s.id, s.erp_code, s.en_name as productEn, s.zh_name as product, lsw.effective_date, lsw.weight FROM sku s LEFT JOIN latestSkuWeights lsw ON s.id = lsw.sku_id AND lsw.rn = 1 WHERE s.status = 3 AND (
s.erp_code REGEXP #{erpCodes}
AND
s.zh_name REGEXP #{zhNames}
AND
s.en_name REGEXP #{enNames}
) ORDER BY ${column} ${order}
LIMIT #{offset}, #{size}
;

SELECT COUNT(*) AS total
FROM sku s
JOIN client_sku ON s.id = client_sku.sku_id
WHERE s.status = 3
    AND client_sku.client_id = #{clientId}
    AND (

            s.erp_code REGEXP #{erpCodes}



                AND

            s.zh_name REGEXP #{zhNames}



                AND

            s.en_name REGEXP #{enNames}

    );

WITH qtyInOrdersNotShippedCTE AS ( SELECT sku_id as ID, SUM(quantity) AS quantity FROM platform_order_content poc JOIN platform_order po ON poc.platform_order_id = po.id JOIN shop s ON po.shop_id = s.id JOIN client c ON s.owner_id = c.id WHERE c.id = #{clientId} AND po.erp_status IN ('1','2') AND poc.erp_status IN ('1','2') GROUP BY sku_id ) SELECT s.id, s.erp_code, s.en_name as productEn, s.zh_name as product, s.purchasing_amount, s.available_amount, qtyInOrdersNotShippedCTE.quantity as qtyInOrdersNotShipped, s.available_amount + s.purchasing_amount - IF(qtyInOrdersNotShippedCTE.quantity IS NULL, 0, qtyInOrdersNotShippedCTE.quantity) as stock, s.image_source, s.service_fee, IF(sp.price_rmb IS NULL, sp.price, ( ROUND( sp.price_rmb / (SELECT rate FROM exchange_rates WHERE original_currency = 'EUR' AND target_currency = 'RMB' ORDER BY create_time DESC LIMIT 1) ,2) ) ) as sku_price, sp.threshold as discount_moq, IF(sp.price_rmb IS NULL, sp.discounted_price, ( ROUND( sp.discounted_price_rmb / (SELECT rate FROM exchange_rates WHERE target_currency = 'EUR' AND original_currency = 'RMB' ORDER BY create_time DESC LIMIT 1) ,2) ) ) as discounted_price, s7.quantity as sales_last_week, s28.quantity as sales_four_weeks, s42.quantity as sales_six_weeks FROM sku s JOIN client_sku ON s.id = client_sku.sku_id LEFT JOIN sku_current_price sp ON s.id = sp.sku_id LEFT JOIN sales_28 s28 ON s.id = s28.sku_id LEFT JOIN sales_42 s42 ON s.id = s42.sku_id LEFT JOIN sales_7 s7 ON s.id = s7.sku_id LEFT JOIN qtyInOrdersNotShippedCTE ON s.id = qtyInOrdersNotShippedCTE.ID WHERE client_sku.client_id = #{clientId} AND s.status = 3 AND (
s.erp_code REGEXP #{erpCodes}
AND
s.zh_name REGEXP #{zhNames}
AND
s.en_name REGEXP #{enNames}
) ORDER BY ${column} ${order}
LIMIT #{offset}, #{size}
;

SELECT id
FROM sku
WHERE erp_code = #{erpCode};

mapper.update:
UPDATE sku
SET update_by = 'mabang api',
    update_time = NOW(),
    available_amount =
    case id

            when #{sku.id} then #{sku.availableAmount}

    end,
    purchasing_amount =
    case id

            when #{sku.id} then #{sku.purchasingAmount}

    end
WHERE id IN

    #{sku.id}
