    erp_code=#{i.erpCode}

mapper.select:
SELECT s.id FROM sku s LEFT JOIN sku_price sp ON s.id = sp.sku_id WHERE sp.id IS NULL AND s.id IN
#{id}
LIMIT 1;

SELECT erp_code
FROM sku
WHERE id IN

    #{skuId}
;

SELECT sku_id as ID, sku.erp_code as erp_code, SUM(quantity) AS quantity FROM platform_order_content JOIN sku ON platform_order_content.sku_id = sku.id WHERE platform_order_id IN
#{orderId}
AND erp_status IN ('1','2','3') AND product_available = 0 AND virtual_product_available = 0 GROUP BY sku_id;

SELECT COUNT(*) AS total
FROM sku s
WHERE s.status = 3;

SELECT COUNT(*) AS total
FROM sku s
WHERE s.status = 3
AND (

        s.erp_code REGEXP #{erpCodes}



            AND

        s.zh_name REGEXP #{zhNames}



            AND

        s.en_name REGEXP #{enNames}

    );

WITH latestSkuWeights AS ( SELECT sku_id, weight, effective_date, ROW_NUMBER() OVER (PARTITION BY sku_id ORDER BY effective_date DESC) AS rn FROM sku_weight ) SELECT s.id, s.erp_code, s.en_name, s.zh_name, lsw.effective_date as weightEffectiveDate, lsw.weight FROM sku s LEFT JOIN latestSkuWeights lsw ON s.id = lsw.sku_id AND lsw.rn = 1 WHERE s.status = 3 AND (
s.erp_code REGEXP #{erpCodes}
AND
s.zh_name REGEXP #{zhNames}
AND
s.en_name REGEXP #{enNames}
) ORDER BY ${column} ${order}
LIMIT #{offset}, #{size}
;

SELECT COUNT(*) AS total
FROM sku s
JOIN client_sku ON s.id = client_sku.sku_id
WHERE s.status = 3
    AND client_sku.client_id = #{clientId}
    AND (

            s.erp_code REGEXP #{erpCodes}



                AND

            s.zh_name REGEXP #{zhNames}



                AND

            s.en_name REGEXP #{enNames}

    );

WITH qtyInOrdersNotShippedCTE AS ( SELECT sku_id as ID, SUM(quantity) AS quantity FROM platform_order_content poc JOIN platform_order po ON poc.platform_order_id = po.id JOIN shop s ON po.shop_id = s.id JOIN client c ON s.owner_id = c.id WHERE c.id = #{clientId} AND po.erp_status IN ('1','2') AND po.can_send = 1 AND poc.erp_status IN ('1','2') GROUP BY sku_id ) SELECT s.id, s.erp_code, s.en_name, s.zh_name, s.purchasing_amount, s.available_amount, qtyInOrdersNotShippedCTE.quantity as qtyInOrdersNotShipped, s.available_amount + s.purchasing_amount - IF(qtyInOrdersNotShippedCTE.quantity IS NULL, 0, qtyInOrdersNotShippedCTE.quantity) as stock, s.image_source, s.service_fee, IF(sp.price_rmb IS NULL, sp.price, ( ROUND( sp.price_rmb / (SELECT rate FROM exchange_rates WHERE original_currency = 'EUR' AND target_currency = 'RMB' ORDER BY create_time DESC LIMIT 1) ,2) ) ) as sku_price, sp.threshold as discount_moq, IF(sp.price_rmb IS NULL, sp.discounted_price, ( ROUND( sp.discounted_price_rmb / (SELECT rate FROM exchange_rates WHERE target_currency = 'EUR' AND original_currency = 'RMB' ORDER BY create_time DESC LIMIT 1) ,2) ) ) as discounted_price, s7.quantity as sales_last_week, s28.quantity as sales_four_weeks, s42.quantity as sales_six_weeks FROM sku s JOIN client_sku ON s.id = client_sku.sku_id LEFT JOIN sku_current_price sp ON s.id = sp.sku_id LEFT JOIN sales_28 s28 ON s.id = s28.sku_id LEFT JOIN sales_42 s42 ON s.id = s42.sku_id LEFT JOIN sales_7 s7 ON s.id = s7.sku_id LEFT JOIN qtyInOrdersNotShippedCTE ON s.id = qtyInOrdersNotShippedCTE.ID WHERE client_sku.client_id = #{clientId} AND s.status = 3 AND (
s.erp_code REGEXP #{erpCodes}
AND
s.zh_name REGEXP #{zhNames}
AND
s.en_name REGEXP #{enNames}
) ORDER BY ${column} ${order}
LIMIT #{offset}, #{size}
;

WITH qtyInOrdersNotShippedCTE AS ( SELECT sku_id as ID, SUM(quantity) AS quantity FROM platform_order_content poc JOIN platform_order po ON poc.platform_order_id = po.id JOIN shop s ON po.shop_id = s.id JOIN client c ON s.owner_id = c.id WHERE c.id = #{clientId} AND po.erp_status IN ('1','2') AND po.can_send = 1 AND poc.erp_status IN ('1','2') GROUP BY sku_id ) SELECT s.id, s.erp_code, s.en_name, s.zh_name, s.purchasing_amount, s.available_amount, qtyInOrdersNotShippedCTE.quantity as qtyInOrdersNotShipped, s.available_amount + s.purchasing_amount - IF(qtyInOrdersNotShippedCTE.quantity IS NULL, 0, qtyInOrdersNotShippedCTE.quantity) as stock, s.image_source, s.service_fee, IF(sp.price_rmb IS NULL, sp.price, ( ROUND( sp.price_rmb / (SELECT rate FROM exchange_rates WHERE original_currency = 'EUR' AND target_currency = 'RMB' ORDER BY create_time DESC LIMIT 1) ,2) )) as sku_price, sp.threshold as discount_moq, IF(sp.price_rmb IS NULL, sp.discounted_price, ( ROUND( sp.discounted_price_rmb / (SELECT rate FROM exchange_rates WHERE target_currency = 'EUR' AND original_currency = 'RMB' ORDER BY create_time DESC LIMIT 1) ,2) )) as discounted_price, s7.quantity as sales_last_week, s28.quantity as sales_four_weeks, s42.quantity as sales_six_weeks FROM sku s JOIN client_sku ON s.id = client_sku.sku_id LEFT JOIN sku_current_price sp ON s.id = sp.sku_id LEFT JOIN sales_28 s28 ON s.id = s28.sku_id LEFT JOIN sales_42 s42 ON s.id = s42.sku_id LEFT JOIN sales_7 s7 ON s.id = s7.sku_id LEFT JOIN qtyInOrdersNotShippedCTE ON s.id = qtyInOrdersNotShippedCTE.ID WHERE client_sku.client_id = #{clientId} AND s.status = 3 AND (
s.erp_code REGEXP #{erpCodes}
AND
s.zh_name REGEXP #{zhNames}
AND
s.en_name REGEXP #{enNames}
) AND ( (sp.price_rmb IS NOT NULL AND sp.price_rmb <> 0) OR (sp.price IS NOT NULL AND sp.price <> 0) ) ORDER BY s.erp_code ;

WITH skusFromInvoice AS ( SELECT sku_id, quantity FROM purchase_order_sku pos JOIN purchase_order po ON pos.purchase_order_id = po.id WHERE po.invoice_number = #{invoiceNumber} ), qtyInOrdersNotShipped AS ( SELECT sku_id as ID, SUM(quantity) AS quantity FROM platform_order_content poc JOIN platform_order po ON poc.platform_order_id = po.id JOIN shop s ON po.shop_id = s.id JOIN client c ON s.owner_id = c.id WHERE sku_id IN (SELECT sku_id FROM skusFromInvoice) AND po.erp_status IN ('1','2') AND poc.erp_status IN ('1','2') GROUP BY sku_id ) SELECT s.id, s.erp_code, s.en_name, s.zh_name, IFNULL(sfi.quantity, 0) as qtyOrdered, s.purchasing_amount as purchasing_amount, s.available_amount as available_amount, IFNULL(qtyInOrdersNotShipped.quantity, 0) as qtyInOrdersNotShipped, IF(s.available_amount IS NULL, 0, s.available_amount) + IF(s.purchasing_amount IS NULL, 0, s.purchasing_amount) - IF(qtyInOrdersNotShipped.quantity IS NULL, 0, qtyInOrdersNotShipped.quantity) as stock, s.image_source as image_source, s.service_fee as service_fee, IF(sp.price_rmb IS NULL, sp.price, ( ROUND( sp.price_rmb / (SELECT rate FROM exchange_rates WHERE original_currency = 'EUR' AND target_currency = 'RMB' ORDER BY create_time DESC LIMIT 1) ,2) ) ) as sku_price, sp.threshold as discount_moq, IF(sp.price_rmb IS NULL, sp.discounted_price, ( ROUND( sp.discounted_price_rmb / (SELECT rate FROM exchange_rates WHERE target_currency = 'EUR' AND original_currency = 'RMB' ORDER BY create_time DESC LIMIT 1) ,2) ) ) as discounted_price, IFNULL(s7.quantity, 0) as sales_last_week, IFNULL(s28.quantity, 0) as sales_four_weeks, IFNULL(s42.quantity, 0) as sales_six_weeks FROM sku s LEFT JOIN sku_price sp ON s.id = sp.sku_id LEFT JOIN sales_28 s28 ON s.id = s28.sku_id LEFT JOIN sales_42 s42 ON s.id = s42.sku_id LEFT JOIN sales_7 s7 ON s.id = s7.sku_id LEFT JOIN qtyInOrdersNotShipped ON s.id = qtyInOrdersNotShipped.ID LEFT JOIN skusFromInvoice sfi ON s.id = sfi.sku_id WHERE s.erp_code IN
#{erpCode}
GROUP BY s.id;

SELECT id
FROM sku
WHERE erp_code = #{erpCode};

mapper.update:
UPDATE sku
SET available_amount =
    case id

            when #{sku.id} then #{sku.availableAmount}

    end,
    purchasing_amount =
    case id

            when #{sku.id} then #{sku.purchasingAmount}

    end
WHERE id IN

    #{sku.id}

mapper.select:
SELECT DISTINCT s.* FROM sku s JOIN client_sku ON s.id = client_sku.sku_id JOIN platform_order_content poc ON s.id = poc.sku_id JOIN platform_order po ON poc.platform_order_id = po.id WHERE client_sku.client_id = #{clientId} AND po.shipping_invoice_number IS NULL AND poc.erp_status <> 5 AND po.erp_status IN
#{erpStatus}
AND po.order_time >= '2024-01-01 00:00:00' AND s.status = 3 ;

SELECT s.id, s.erp_code, s.en_name, s.zh_name, s.purchasing_amount, s.available_amount, s.image_source, s.service_fee, IF(sp.price_rmb IS NULL, sp.price, ( ROUND( sp.price_rmb / (SELECT rate FROM exchange_rates WHERE original_currency = 'EUR' AND target_currency = 'RMB' ORDER BY create_time DESC LIMIT 1) ,2) ) ) as sku_price, sp.threshold as discount_moq, IF(sp.price_rmb IS NULL, sp.discounted_price, ( ROUND( sp.discounted_price_rmb / (SELECT rate FROM exchange_rates WHERE target_currency = 'EUR' AND original_currency = 'RMB' ORDER BY create_time DESC LIMIT 1) ,2) ) ) as discounted_price FROM sku s LEFT JOIN sku_current_price sp ON s.id = sp.sku_id WHERE s.status = 3 AND (
s.erp_code LIKE CONCAT('%', #{keyword}, '%') OR s.en_name LIKE CONCAT('%', #{keyword}, '%') OR s.zh_name LIKE CONCAT('%', #{keyword}, '%')
)

SELECT erp_code
FROM sku s
WHERE erp_code LIKE CONCAT(#{date}, #{userCode}, '%%%-', #{clientCode});

mapper.update:
