mapper.select:
SELECT *
FROM sku_weight
WHERE sku_id = #{skuId}
ORDER BY effective_date DESC
LIMIT 1;

SELECT s.id FROM sku s LEFT JOIN sku_weight sw ON sw.sku_id = s.id WHERE sw.id IS NULL AND s.id IN ( SELECT DISTINCT id FROM sku WHERE id IN
#{id}
) LIMIT 1;

WITH latestSkuWeights AS ( SELECT sku_id, weight, effective_date, ROW_NUMBER() OVER (PARTITION BY sku_id ORDER BY effective_date DESC) AS rn FROM sku_weight ) SELECT s.erp_code, lsw.effective_date, lsw.weight FROM sku s LEFT JOIN latestSkuWeights lsw ON s.id = lsw.sku_id AND lsw.rn = 1 WHERE s.status = 3 AND sku_id IN
#{skuId}
ORDER BY erp_code;
