mapper.select:
select * from sys_user where del_flag = 0

	and id in (select user_id from sys_user_depart where dep_id in

		#{id}

	)


	and username = #{username}

mapper.update:
UPDATE sys_user SET

	 org_code = #{orgCode}
	,login_tenant_id = #{loginTenantId}


	login_tenant_id = #{loginTenantId}


	org_code = #{orgCode}

where username = #{username}

delete from sys_user_role
where role_id in

	#{id}

delete from sys_role_permission
where role_id in

	#{id}

mapper.select:
select * from sys_user where del_flag = 0

	and id in (select user_id from sys_user_depart where dep_id in

		#{id}

	)


	and username != #{username}

select DISTINCT a.* from sys_user a left join sys_user_role sur on sur.user_id = a.id where a.del_flag = 0 and a.status = 1 and a.username!='_reserve_user_external' and sur.role_id=#{roleId}
and (a.username like #{bindKeyword} or a.realname like #{bindKeyword})
and a.id in ( select user_id from sys_user_tenant where tenant_id = #{tenantId} and status = '1' )

		select su.* from sys_user su
		join sys_user_tenant sut on su.id = sut.user_id
		where su.del_flag = 0

			and su.id in (select user_id from sys_user_depart
			where dep_id in

				#{id}

			)

		and sut.tenant_id=#{tenantId}
        and sut.status = '1'
