mapper.select:
select f.id,f.user_id,f.to_user_id,f.is_star,is_no_disturb,is_msg_archive,remark,phone,info,tag_ids,tag_names,tr, add_type, f.status, privilege, f.ts_create,f.ts_friend,f.ts_pin, f.ts_update, f.ts_last_talk,f.ts_msg_visible,f.ts_black,f.ts_been_black,f.is_hide,f.is_unread,f.is_read_del,f.last_ack_id, t.id t_id,t.account t_account,t.nickname t_nickname,t.username t_username,t.type t_type,t.is_online t_is_online,t.small_avatar t_small_avatar,t.avatar t_avatar, info.gender t_gender,info.country t_country,info.province t_province,info.city t_city,info.signature t_signature,info.state t_state from im_friend f left join im_user t on t.id = f.to_user_id left join im_user_info info on info.user_id = t.id
1=1
and f.user_id = #{q.userId}
and f.add_type = #{q.addType}
and f.remark = #{q.remark}
and(t.nickname like concat('%',#{q.userSearch},'%') or t.account like concat('%',#{q.userSearch},'%') or t.username like concat('%',#{q.userSearch},'%') or t.mobile like concat('%',#{q.userSearch},'%'))
and f.is_star = #{q.isStar}
and f.ts_create <= UNIX_TIMESTAMP(#{q.ts_create})*1000
order by f.ts_create desc

select f.id,f.user_id,f.to_user_id,f.is_star,is_no_disturb,is_msg_archive,remark,phone,info,tag_ids,tag_names,tr, add_type, f.status, privilege, f.ts_create,f.ts_friend,f.ts_pin, f.ts_update, f.ts_last_talk,f.ts_msg_visible,f.ts_black,f.ts_been_black,f.is_hide,f.is_unread,f.is_read_del,f.last_ack_id, t.id t_id,t.account t_account,t.nickname t_nickname,t.username t_username,t.type t_type,t.small_avatar t_small_avatar,t.avatar t_avatar,t.ts_online t_ts_online,t.ts_offline t_ts_offline,t.is_online t_is_online, info.gender t_gender,info.country t_country,info.province t_province,info.city t_city,info.signature t_signature,info.state t_state,setting.online_visibility from im_friend f left join im_user t on t.id = f.to_user_id left join im_user_info info on info.user_id = t.id left join im_user_setting setting on setting.user_id = t.id
f.user_id = #{userId}
and to_user_id = #{toUserId}
order by f.ts_create desc
