mapper.select:
select ga.id, name, ga.add_times,ga.order_no,ga.ts_create,
count(g.id) counts
from im_gif_album ga
left join im_gif g on g.album_id = ga.id
1=1

        and ga.name = #{q.name}


        and ga.add_times >= #{q.addTimes}


        and ga.ts_create <= UNIX_TIMESTAMP(#{q.ts_create})*1000


group by ga.id

    order by ${q.column} ${q.order}

SELECT a.id,a.name,a.counts,a.send_times,a.ts_create FROM (
    SELECT ga.id,ga.name,sum(send_times) send_times,SUM(g.add_times) add_times,COUNT(g.id) counts,ga.ts_create FROM im_gif_album ga RIGHT JOIN im_gif g ON ga.id = g.album_id GROUP BY ga.id ORDER BY send_times desc,add_times desc,ga.order_no desc
)a
