mapper.select:
select m.id, m.origin_id,m.content,m.body, m.type,m.stanza_id,m.is_encrypt,m.ts_delete,m.ts_send,m.ts_read,m.is_spam,m.ts_pin,m.ts_revoke,m.revoke_type, fu.id fu_id,fu.nickname fu_nickname,fu.account fu_account,fu.small_avatar fu_small_avatar,fu.is_online fu_is_online,fu.ts_create fu_ts_create, fi.id fi_id,fi.province fi_province,fi.city fi_city,fi.country fi_country,fi.district fi_district,fi.gender fi_gender, tu.id tu_id,tu.nickname tu_nickname,tu.account tu_account,tu.small_avatar tu_small_avatar,tu.is_online tu_is_online,tu.ts_create tu_ts_create, ti.id ti_id,ti.province ti_province,ti.city ti_city,ti.country ti_country,ti.district ti_district,ti.gender ti_gender from im_msg m left join im_user fu on fu.id = m.user_id left join im_user_info fi on fi.user_id = fu.id left join im_user tu on tu.id = m.to_user_id left join im_user_info ti on ti.user_id = tu.id is_send is true
and m.ts_delete > 0
and m.type = #{q.type}
and m.content like concat("%",#{q.content},"%")
and m.is_spam = #{q.isSpam}
and m.ts_pin >0
and m.ts_pin = 0
and ts_send >= #{q.sendStartTime}
and ts_send <= #{q.sendEndTime}
and (fu.account = #{q.sender} or fu.mobile = #{q.sender} or fu.username = #{q.sender} or fu.nickname like concat("%",#{q.sender},"%") or fu.id = #{q.sender})
and (tu.account = #{q.receiver} or tu.mobile = #{q.receiver} or tu.username = #{q.receiver} or tu.nickname like concat("%",#{q.receiver},"%") or tu.id = #{q.receiver})
and m.ts_create <= UNIX_TIMESTAMP(#{q.ts_create})*1000
order by ${q.column} ${q.order}

select a.* from( select m.id,m.origin_id,m.content,m.body,stanza_id,m.type,m.is_encrypt,m.is_spam,m.ts_send,m.ts_read,m.is_send,m.is_read_del,m.ts_revoke,m.revoke_type,m.ts_pin, fu.id fu_id,fu.nickname fu_nickname,fu.account fu_account,fu.small_avatar fu_small_avatar, tu.id tu_id,tu.nickname tu_nickname,tu.account tu_account,tu.small_avatar tu_small_avatar from im_msg m left join im_user fu on fu.id = m.user_id left join im_user tu on tu.id = m.to_user_id left join im_friend f on f.user_id = fu.id and f.to_user_id = tu.id ts_delete = 0 and ((m.user_id = #{q.userId} and m.to_user_id = #{q.toUserId} and is_send is true)or (m.to_user_id = #{q.userId} and m.user_id = #{q.toUserId} and is_send is false))
and m.ts_send < #{q.tsSend}
and m.ts_send >= #{q.tsSend}
order by m.ts_send desc limit #{q.pageSize} )a

mapper.update:
update im_msg set ts_delete = #{tsDelete}


            where id in (#{ids})


            where id in (#{ids}) or origin_id in (#{ids})
