mapper.select:
select m.id, m.nickname,m.user_id, m.title,m.back_img,m.is_msg_archive,ts_msg_visible, m.ts_join, m.ts_pin, m.is_no_disturb,m.is_hide,m.is_unread,m.is_read_del,m.unread_count,m.join_type,
       m.role, m.level_id, m.coin,m.ts_mute,ts_mute_begin,mute_type,m.status,m.kicker,m.ts_quit,remark,last_ack_id,
user.nickname user_nickname,user.account user_account,user.small_avatar user_small_avatar,
p.id p_id, p.muc_id p_muc_id, p.manager_id p_manager_id, p.modify_info p_modify_info, p.modify_notice p_modify_notice, p.msg_pin p_msg_pin, p.add_member p_add_member, p.del_member p_del_member, p.mute_member p_mute_member, p.add_manager p_add_manager, p.revoke_manager p_revoke_manager, p.is_validation_tip p_is_validation_tip,p.is_anonymous p_is_anonymous
from im_muc_member m
left join im_user user on user.id = m.user_id
left join im_muc_permission p on p.manager_id = m.id
1=1

        and m.muc_id = #{q.mucId}


        and m.user_id = #{q.userId}


        and user.account = #{q.userAccount}




                and m.ts_mute = #{q.tsMute}


                and m.ts_mute >= #{q.tsMute}






                and m.ts_mute_begin = #{q.tsMuteBegin}


                and m.ts_mute_begin >= #{q.tsMuteBegin}




        and m.mute_type = #{q.muteType}


        and m.role = #{q.role}


        and m.ts_create <= UNIX_TIMESTAMP(#{q.ts_create})*1000



    order by ${q.column} ${q.order}

select m.id, m.nickname,m.user_id,m.muc_id,m.title, m.back_img,m.is_msg_archive,ts_msg_visible, m.ts_join, m.ts_pin, m.is_no_disturb,m.is_hide,m.is_unread,m.is_read_del,m.unread_count,m.join_type, m.role, m.level_id, m.coin,m.ts_mute,m.ts_mute_begin,m.mute_type,m.status,m.kicker,m.ts_quit,remark,last_ack_id, user.nickname user_nickname,user.small_avatar user_small_avatar, p.id p_id, p.muc_id p_muc_id, p.manager_id p_manager_id, p.modify_info p_modify_info, p.modify_notice p_modify_notice, p.msg_pin p_msg_pin, p.add_member p_add_member, p.del_member p_del_member, p.mute_member p_mute_member, p.add_manager p_add_manager, p.revoke_manager p_revoke_manager, p.is_validation_tip p_is_validation_tip,p.is_anonymous p_is_anonymous from im_muc_member m left join im_user user on m.user_id = user.id left join im_muc_permission p on p.manager_id = m.id
m.muc_id = #{q.mucId} and m.status=#{q.status}
order by role desc,ts_join desc,user.is_online desc
