mapper.select:
select
s.id, s.name,s.info,s.zip,s.ts_create,s.add_times,s.is_locked,s.is_animated,s.is_big_emoji,s.is_emoji,s.is_hot,s.order_no,
user.id user_id,user.nickname user_nickname,user.account user_account,user.small_avatar user_small_avatar,
count(i.id) counts,sum(i.send_times) total_send_times,
i.id i_id, emoji i_emoji, emoji_code i_emoji_code,origin i_origin,thumb i_thumb, keyword i_keyword,i.order_no i_order_no, i.ts_create i_ts_create,zip_name i_zip_name,lottie i_lottie,send_times i_send_times
from im_sticker s
left join im_user user on user.id = s.user_id
left join im_sticker_item i on i.sticker_id = s.id
1=1

        and s.is_animated = #{q.isAnimated}


        and s.is_big_emoji = #{q.isBigEmoji}


        and s.is_emoji = #{q.isEmoji}


        and s.name = #{q.name}


        and user.account = #{q.userAccount}


        and s.add_times >= #{q.addTimes}


        and s.is_locked >= #{q.isLocked}


        and s.ts_create <= UNIX_TIMESTAMP(#{q.ts_create})*1000


group by s.id

    order by ${q.column} ${q.order}

select s.id, s.name,s.info,s.zip,s.add_times,s.is_locked, s.ts_create,s.is_animated,s.is_big_emoji,s.is_emoji,s.is_hot,s.order_no, user.id user_id,user.nickname user_nickname,user.account user_account,user.small_avatar user_small_avatar, i.id i_id, emoji i_emoji, emoji_code i_emoji_code,origin i_origin,thumb i_thumb, keyword i_keyword,i.order_no i_order_no, i.ts_create i_ts_create,zip_name i_zip_name,lottie i_lottie,sticker_id i_sticker_id, a.* from im_sticker s left join im_user user on user.id = s.user_id left join im_sticker_item i on i.sticker_id = s.id left join ( select ss.id ss_id,ss.sticker_item_id ss_sticker_item_id,ss.ts_last_send ss_ts_last_send,ss.user_id ss_user_id,ss.times ss_times from im_sticker_send ss left join im_user u on u.id = ss.user_id 1=1
and ss.user_id = #{q.senderId}
)a on a.ss_sticker_item_id = i.id 1=1
and ( s.name like concat("%",#{q.search},"%") or s.info like concat("%",#{q.search},"%") or i.emoji like concat("%",#{q.search},"%") or i.emoji_code like concat("%",#{q.search},"%") )
order by s.is_hot is true desc,s.order_no desc,s.ts_create desc
